package com.amazon.cmd;import com.amazonaws.AmazonClientException;import com.amazonaws.AmazonServiceException;public class S3ExceptionService {    public static String getOutputMessage(AmazonClientException e){        StringBuilder sb=new StringBuilder();        if (e.getClass().isInstance(AmazonServiceException.class)){            AmazonServiceException ex = (AmazonServiceException)e;            sb.append("Caught an AmazonServiceException, which means your request made it ")                    .append("to Amazon S3, but was rejected with an error response for some reason.")                    .append( "Error Message:    " + ex.getMessage())                    .append( "HTTP Status Code: " + ex.getStatusCode())                    .append( "AWS Error Code:   " + ex.getErrorCode())                    .append( "Error Type:       " + ex.getErrorType())                    .append("Request ID:       " + ex.getRequestId());        } else {            sb.append("Caught an AmazonClientException, which means the client encountered ")                    .append("an internal error while trying to communicate with S3, such as not being able ")                    .append("to access the network.")                    .append("Error Message: " + e.getLocalizedMessage());        }        return sb.toString();    }}